import React, { useState, useEffect } from 'react';

function App() {
  // State: Notlar ve etiketler için yeni bir yapı
  const [notes, setNotes] = useState([]);
  const [noteText, setNoteText] = useState('');
  const [tagText, setTagText] = useState(''); // Yeni eklenen etiket için state

  // Sayfa yüklendiğinde localStorage'dan notları ve etiketleri yükler
  useEffect(() => {
    const savedNotes = JSON.parse(localStorage.getItem('notes'));
    if (savedNotes) {
      setNotes(savedNotes);
    }
  }, []);

  // Notlar değiştiğinde localStorage'a kaydeder
  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  // Yeni not ekleme fonksiyonu (not metni ve etiket ekleniyor)
  const addNote = () => {
    if (noteText.trim() !== '' && tagText.trim() !== '') {
      setNotes([...notes, { text: noteText, tag: tagText }]); // Not ve etiket nesne olarak ekleniyor
      setNoteText(''); // input alanını temizle
      setTagText(''); // etiket alanını temizle
    }
  };

  // Notu silme fonksiyonu
  const deleteNote = (index) => {
    const newNotes = notes.filter((_, i) => i !== index);
    setNotes(newNotes);
  };

  return (
    <div className="App" style={styles.container}>
      <h1>Not Alma Uygulaması</h1>

      <div style={styles.inputContainer}>
        <input
          type="text"
          value={noteText}
          onChange={(e) => setNoteText(e.target.value)}
          placeholder="Notunuzu yazın"
          style={styles.input}
        />
        <input
          type="text"
          value={tagText}
          onChange={(e) => setTagText(e.target.value)}
          placeholder="Etiket ekleyin (örn: İş, Kişisel)"
          style={styles.input}
        />
        <button onClick={addNote} style={styles.addButton}>
          Ekle
        </button>
      </div>

      <div style={styles.notesContainer}>
        {notes.length === 0 ? (
          <p>Henüz not eklenmedi.</p>
        ) : (
          notes.map((note, index) => (
            <div key={index} style={styles.note}>
              <div>
                <p><strong>Not:</strong> {note.text}</p>
                <p><strong>Etiket:</strong> {note.tag}</p>
              </div>
              <button onClick={() => deleteNote(index)} style={styles.deleteButton}>
                Sil
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

// Basit stiller
const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    marginTop: '50px',
  },
  inputContainer: {
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    width: '300px',
    fontSize: '16px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    marginBottom: '10px',
  },
  addButton: {
    padding: '10px 20px',
    marginLeft: '10px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  notesContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  note: {
    backgroundColor: '#f9f9f9',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '4px',
    width: '300px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default App;
