import React, { useState, useEffect } from 'react';

function App() {
  const [notes, setNotes] = useState([]);
  const [noteText, setNoteText] = useState('');
  const [tagText, setTagText] = useState('');

  useEffect(() => {
    const savedNotes = JSON.parse(localStorage.getItem('notes'));
    if (savedNotes) {
      setNotes(savedNotes);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  const addNote = () => {
    if (noteText.trim() !== '' && tagText.trim() !== '') {
      setNotes([...notes, { text: noteText, tag: tagText }]);
      setNoteText('');
      setTagText('');
    }
  };

  const deleteNote = (index) => {
    const newNotes = notes.filter((_, i) => i !== index);
    setNotes(newNotes);
  };

  return (
    <div className="App" style={styles.container}>
      <h1 style={styles.title}><i className="fas fa-sticky-note"></i> Not Alma Uygulaması</h1>

      <div style={styles.inputContainer}>
        <div style={styles.inputWrapper}>
          <i className="fas fa-pencil-alt" style={styles.icon}></i>
          <input
            type="text"
            value={noteText}
            onChange={(e) => setNoteText(e.target.value)}
            placeholder="Notunuzu yazın"
            style={styles.input}
          />
        </div>
        <div style={styles.inputWrapper}>
          <i className="fas fa-tag" style={styles.icon}></i>
          <input
            type="text"
            value={tagText}
            onChange={(e) => setTagText(e.target.value)}
            placeholder="Etiket ekleyin (örn: İş, Kişisel)"
            style={styles.input}
          />
        </div>
        <button onClick={addNote} style={styles.addButton}>
          <i className="fas fa-plus-circle"></i> Ekle
        </button>
      </div>

      <div style={styles.notesContainer}>
        {notes.length === 0 ? (
          <p style={styles.noNotes}>Henüz not eklenmedi.</p>
        ) : (
          notes.map((note, index) => (
            <div key={index} style={styles.note}>
              <div>
                <p style={styles.noteText}>
                  <i className="fas fa-sticky-note"></i> <strong>Not:</strong> {note.text}
                </p>
                <p style={styles.tag}>
                  <i className="fas fa-tag"></i> <strong>Etiket:</strong> {note.tag}
                </p>
              </div>
              <button onClick={() => deleteNote(index)} style={styles.deleteButton}>
                <i className="fas fa-trash"></i> Sil
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

