import React, { useState, useEffect } from 'react';

function App() {
  // State where we will store notes
  const [notes, setNotes] = useState([]);
  const [noteText, setNoteText] = useState('');

  // Loads notes from localStorage when page loads
  useEffect(() => {
    const savedNotes = JSON.parse(localStorage.getItem('notes'));
    if (savedNotes) {
      setNotes(savedNotes);
    }
  }, []);

  // Saves notes to localStorage when changed
  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  // add new note function
  const addNote = () => {
    if (noteText.trim() !== '') {
      setNotes([...notes, noteText]);
      setNoteText(''); // input alan覺n覺 temizle
    }
  };

  // delete note function
  const deleteNote = (index) => {
    const newNotes = notes.filter((_, i) => i !== index);
    setNotes(newNotes);
  };

  return (
    <div className="App" style={styles.container}>
      <h1>Note Taking App</h1>

      <div style={styles.inputContainer}>
        <input
          type="text"
          value={noteText}
          onChange={(e) => setNoteText(e.target.value)}
          placeholder="Here write note"
          style={styles.input}
        />
        <button onClick={addNote} style={styles.addButton}>
          Add
        </button>
      </div>

      <div style={styles.notesContainer}>
        {notes.length === 0 ? (
          <p>Not yet add note</p>
        ) : (
          notes.map((note, index) => (
            <div key={index} style={styles.note}>
              <p>{note}</p>
              <button onClick={() => deleteNote(index)} style={styles.deleteButton}>
                Sil
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

// Basit stiller
const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    marginTop: '50px',
  },
  inputContainer: {
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    width: '300px',
    fontSize: '16px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  addButton: {
    padding: '10px 20px',
    marginLeft: '10px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  notesContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  note: {
    backgroundColor: '#f9f9f9',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '4px',
    width: '300px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default App;
